import React, { Component } from 'react';
import logo from './logo.png';
import './App.css';
import ReactSpeedometer from 'react-d3-speedometer';


class App extends Component {
  constructor(props){
    super(props)
    this.state = {
      city : null,
      aqi : null,
      render : false
    }
    this.handleSubmit = this.handleSubmit.bind(this)
  }
  handleChange = e => {
    e.preventDefault()
    this.setState({
      city : e.target.value
    })
  }

  async handleSubmit(e) {
    e.preventDefault()
    this.setState({
      render : true
    })
    var request = {
      method : "GET"
    }
    const res = await fetch(`https://api.waqi.info/feed/${this.state.city}/?token=1d58f819c0fca4b883949d58334c3b4adc6889db`, request)
    const data = await res.json()
    this.setState({
      aqi : data.data.aqi
    })
  }


  render() {
    const h1Style = {
      margin : '20px',
      border : '3px solid pink'
    }

    const divStyle = {
      padding : '30px'
    }
    return (
      <div className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <form>
            <b><i>City</i></b> &nbsp;&nbsp;
            <input type="text" value={this.state.city || ""} onChange={this.handleChange} />&nbsp;&nbsp;
            <button type="submit" value="Search" onClick={this.handleSubmit}>Search</button>
          </form>
          {this.state.render && <h1 style={h1Style}>{this.state.aqi}</h1>}
          <div style={divStyle}>
            <ReactSpeedometer
            maxValue={400}
            startColor="green"
            value={this.state.aqi}
            segments={5}
            endColor="red"
            />
          </div>

      </div>
    );
  }
}

export default App;
