import React, { Component } from 'react';
import logo from './logo.png';
import './App.css';

class App extends Component {
  constructor(props){
    super(props)
    this.state = {
      city : null,
      aqi : null,
      render : false
    }
    this.handleSubmit = this.handleSubmit.bind(this)
  }
  handleChange = e => {
    e.preventDefault()
    this.setState({
      city : e.target.value
    })
  }

  async handleSubmit(e) {
    e.preventDefault()
    this.setState({
      render : true
    })
    var request = {
      method : "GET"
    }
    const res = await fetch(`https://api.waqi.info/feed/${this.state.city}/?token=1d58f819c0fca4b883949d58334c3b4adc6889db`, request)
    const data = await res.json()
    this.setState({
      aqi : data.data.aqi
    })
  }


  render() { 
    return (
      <div className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <form>
            City &nbsp;&nbsp;
            <input type="text" value={this.state.city || ""} onChange={this.handleChange} />&nbsp;&nbsp;
            <button type="submit" value="Search" onClick={this.handleSubmit}>
              <p style="margin-left: 2.5em;padding: 0 7em 2em 0;border-width: 2px; border-color: black; border-style:solid;"></p>Search
            </button>
          </form>
          {this.state.render && <h1>{this.state.aqi}</h1>}
      </div>
    );
  }
}

export default App;
